import re 
def countWords(fname):
    f = open(fname)
    totwords = 0
    word_dict = {}

    for l in f:
        l = re.sub('[\.;,:"\(\)]',' ',l)
        words = l.split()
        for word in words:
            totwords = totwords + 1
            if word in word_dict:
                word_dict[word] = word_dict[word] + 1 
            else:
                word_dict[word] = 1
    f.close()

    df = pd.DataFrame.from_dict(dict(sorted(word_dict.items(),key=lambda item: item[1],reverse=True)), orient='index')
    return df

